Ext.define('MyApp.controller.AccountController', {
    extend: 'Ext.app.Controller',

    login: function(target) {

        // Create new login form window
        var login = Ext.create("MyApp.view.LoginForm");

        // Show window
        login.show();

    },

    register: function(target) {

        // Create new register form window
        var register = Ext.create("MyApp.view.Register");

        // Show window
        register.show();

    },

    init: function(application) {
        this.control({
            "mainview #loginButton": {
                click: this.login
            },
            "mainview #registerForm": {
                click: this.register
            }
        });
    }

});
